export default {
  name: 'HelloWorld',
  data: function data() {
    return {
      container: null,
      blitzboard: null,
      config: null,
      graph: null
    };
  },
  created: function created() {},
  mounted: function mounted() {
    this.container = document.getElementById('blitzboard');
    this.blitzboard = new Blitzboard(this.container); // no-undef

    this.config = {
      node: {
        caption: ['id'],
        icon: {
          'Property': 'f38f',
          'Tax': 'f26d',
          'Account': 'f119',
          'Income': 'f316'
        }
      },
      edge: {
        caption: ['label']
      }
    }; // pg를 통해서 그래프 생성

    this.blitzboard.setGraph(this.graph, false);
    this.blitzboard.setConfig(this.config);
  },
  methods: {
    update: function update() {
      var _this = this;

      // sample
      this.graph = 'I :person name:"your name" \n' + 'You :person \n' + 'Graph :graph \n' + 'I -> Graph :say word:Hello date:today \n' + 'You -> I :say word:Goodbye date:yesterday \n';
      this.blitzboard.setGraph(this.graph, true); //this.blitzboard.setConfig(this.config);

      this.blitzboard.network.on("click", function (e) {
        if (e.nodes.length > 0) {
          var node = _this.blitzboard.nodeMap[e.nodes[0]];

          _this.blitzboard.scrollNodeIntoView(node); // 해당 노드를 하이라이트하여 중앙으로 이동시킴

        }
      });
    }
  }
};
