      }],
      selectedLabel: null,
      selectedIcon: null,
      selectedShape: null,
      selectedSize: 0,
      graphItems: [{
        text: "종목",
        value: "G_NH_STOCK"
      }, {
        text: "제품",
        value: "G_NH_PRODUCT"
      }, {
        text: "인물",
